import {useState} from "react"
import TextField from "@mui/material/TextField";
import Button from "@mui/material/Button";
const AddPost = () => {
  return (
    <form method="post">
      <TextField
        label="Post Title"
        fullWidth
        sx={{ mb: 2 }}
        name="title"
        value="title value"
        helperText="Post title must contain 7-70 chars"
      />
      <TextField
        label="Post Content"
        name="body"
        value="body value"
        fullWidth
        multiline
        rows={6}
        sx={{ mb: 2 }}
        helperText="Post content must contain 7-2000 chars"
      />
      <Button fullWidth variant="contained" type="submit">
        Submit
      </Button>
    </form>
  );
};

export default AddPost;
